import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Items from './components/Items';

class App extends React.Component {
constructor(props){
  super(props)
  this.state = {
    orders: [],
    items: [
      {
        id: 1,
        title: 'Стул серый',
        img: 'chair-grey.jpeg',
        desc: 'Lorem ipsum dolor sit amet.',
        category: 'chairs',
        price: '49.99'
      },
      {
        id: 2,
        title: 'Стол',
        img: 'table.webp',
        desc: 'Lorem ipsum dolor sit amet.',
        category: 'tables',
        price: '149.99'
      },
      {
        id: 3,
        title: 'Стул серый',
        img: 'chair-grey.jpeg',
        desc: 'Lorem ipsum dolor sit amet.',
        category: 'chairs',
        price: '49.99'
      },
      {
        id: 4,
        title: 'Диван',
        img: 'sofa.jpeg',
        desc: 'Lorem ipsum dolor sit amet.',
        category: 'sofa',
        price: '549.99'
      },
      {
        id: 5,
        title: 'Лампа',
        img: 'wall-light.jpeg',
        desc: 'Lorem ipsum dolor sit amet.',
        category: 'light',
        price: '29.99'
      },
      {
        id: 6,
        title: 'Стул белый',
        img: 'chair-white.jpeg',
        desc: 'Lorem ipsum dolor sit amet.',
        category: 'chairs',
        price: '49.99'
      } 
    ]
  }
  this.addToOrder = this.addToOrder.bind(this)
}
  render (){
  return (
   <div className="wrapper">
      <Header/>
      <Items items={this.state.items} onAdd={this.addToOrder}/>
      <Footer/>
   </div>
  );
}

addToOrder(item){
  this.setState({orders: [...this.state.orders, item]})
}
}

export default App;
